mapper.select:
		SELECT *
  		  FROM (SELECT ROWNUM R_NUM, B.*
          	      FROM (SELECT *
                  		  FROM BBS_T 
                 		 ORDER BY B_IDX DESC
                	   ) B
       	   	   )
 	    WHERE R_NUM BETWEEN #{begin} AND #{end}

SELECT B_IDX, SUBJECT, WRITER, CONTENT, FILE_NAME, ORI_NAME, PWD, WRITE_DATE, IP, HIT
  FROM BBS_T
 WHERE B_IDX = #{b_idx}

SELECT C_IDX, WRITER, CONTENT, PWD, WRITE_DATE, IP, B_IDX
  FROM COMMENT_T 
 WHERE B_IDX = #{b_idx}
